import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

let socket; // define it outside, but connect inside

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    socket = io('http://localhost:5000'); // connect inside useEffect
    console.log('âœ… Connected to backend');

    socket.on('bot message', (message) => {
      console.log('ðŸ“© Bot message received:', message);
      setMessages((prev) => [...prev, { sender: 'bot', text: message }]);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (input.trim() === '') return;

    setMessages((prev) => [...prev, { sender: 'user', text: input }]);
    socket.emit('chat message', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', marginTop: '30px' }}>
      <h2>Museum Ticket Chatbot</h2>
      <div style={{ border: '1px solid gray', padding: '20px', minHeight: '400px', overflowY: 'auto' }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{
            textAlign: msg.sender === 'user' ? 'right' : 'left',
            margin: '10px 0'
          }}>
            <span style={{
              display: 'inline-block',
              background: msg.sender === 'user' ? '#c8e6c9' : '#e0e0e0',
              padding: '10px',
              borderRadius: '10px',
              maxWidth: '70%',
              wordWrap: 'break-word',
            }}>
              {msg.text}
            </span>
          </div>
        ))}
      </div>

      <div style={{ display: 'flex', marginTop: '10px' }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          style={{ flexGrow: 1, padding: '10px' }}
        />
        <button onClick={sendMessage} style={{ padding: '10px', background: 'green', color: 'white', border: 'none' }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBot;
