import React, { useContext } from "react";

import { ThemeContext } from "../App"; // Import the ThemeContext
import ChatBot from "./ChatBot"; // Your existing chatbot

function Chat() {
  const { theme } = useContext(ThemeContext);
    
      const isDark = theme === "dark"; // Get the current theme from context

  // Define the container style based on the theme
  const containerStyle = {
    padding: "20px",
    backgroundColor: theme === "dark" ? "#1f2937" : "#f7f7f7", // Set background color based on theme
    minHeight: "100vh", // Ensure the container fills the entire height of the screen
    color: theme === "dark" ? "#ffffff" : "#000000", // Set text color based on theme
  };

  return (
    <div style={containerStyle}>
      <h2 style={{ textAlign: "center",color: isDark ? "#90ee90" : "#2E8B57",marginTop:"70px" }}>Start Chatting with PEAS!</h2>
      <ChatBot isDarkTheme={theme === "dark"} /> {/* Pass the theme to ChatBot */}
    </div>
  );
}

export default Chat;
