import React from "react";

function Home() {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Welcome to PEAS</h1>
      <p style={styles.subtitle}>
        Your Personal Assistant for Effortless Museum Ticket Booking!
      </p>

      <div style={styles.features}>
        <div style={styles.featureCard}>
          <h2>üé´ Easy Booking</h2>
          <p>Book your museum tickets quickly with a friendly chatbot!</p>
        </div>
        <div style={styles.featureCard}>
          <h2>üèõÔ∏è Explore Museums</h2>
          <p>Discover exciting museums and exhibitions around you.</p>
        </div>
        <div style={styles.featureCard}>
          <h2>üí¨ Instant Support</h2>
          <p>Get instant answers to your museum ticket queries with PEAS.</p>
        </div>
      </div>

      <button style={styles.button} onClick={() => window.location.href='/chat'}>
        Start Chatting
      </button>
    </div>
  );
}

const styles = {
  container: {
    textAlign: "center",
    padding: "50px",
    backgroundColor: "#f5f5f5",
    minHeight: "90vh",
  },
  title: {
    fontSize: "48px",
    color: "#2E8B57",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "20px",
    color: "#555",
    marginBottom: "40px",
  },
  features: {
    display: "flex",
    justifyContent: "center",
    gap: "30px",
    marginBottom: "50px",
    flexWrap: "wrap",
  },
  featureCard: {
    backgroundColor: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
    width: "250px",
  },
  button: {
    backgroundColor: "#2E8B57",
    color: "white",
    padding: "15px 30px",
    fontSize: "18px",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
  },
};

export default Home;
