import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";
import { motion, AnimatePresence } from "framer-motion";

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { sender: 'bot', text: 'ðŸ‘‹ Hello! I am PEAS, your guide assistant. Please type "hi" to start the conversation!' }
  ]);
  const [input, setInput] = useState("");
  const chatContainerRef = useRef(null);

  useEffect(() => {
    socket.on('bot message', (message) => {
      setMessages((prev) => [...prev, { sender: 'bot', text: message }]);
    });

    return () => {
      socket.off('bot message');
    };
  }, []);

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === '') return;

    setMessages((prev) => [...prev, { sender: 'user', text: input }]);
    socket.emit('chat message', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  const resetChat = () => {
    setMessages([
      { sender: 'bot', text: 'ðŸ‘‹ Hello! I am PEAS, your guide assistant. Please type "hi" to start the conversation!' }
    ]);
  };

  const renderMessage = (msg) => {
    if (msg.text.includes('<img')) {
      const imgSrc = msg.text.match(/src="(.*?)"/)[1];
      return <img src={imgSrc} alt="QR Code" style={{ maxWidth: '100%', marginTop: '10px' }} />;
    }
    return msg.text;
  };

  return (
    <div style={{
      maxWidth: '600px',
      margin: '30px auto',
      backgroundColor: '#f7f7f7',
      borderRadius: '10px',
      boxShadow: '0px 4px 10px rgba(0,0,0,0.1)',
      display: 'flex',
      flexDirection: 'column',
      height: '90vh'
    }}>
      {/* Header */}
      <div style={{
        padding: '20px',
        textAlign: 'center',
        backgroundColor: '#4CAF50',
        borderTopLeftRadius: '10px',
        borderTopRightRadius: '10px',
        color: 'white'
      }}>
        <h2>PEAS - Your Museum Ticket Guide</h2>
      </div>

      {/* Chat Area */}
      <div ref={chatContainerRef} style={{
        flexGrow: 1,
        overflowY: 'auto',
        padding: '20px',
        display: 'flex',
        flexDirection: 'column',
        gap: '10px'
      }}>
        <AnimatePresence>
          {messages.map((msg, idx) => (
            <motion.div
              key={idx}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 0.3 }}
              style={{
                alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
                background: msg.sender === 'user' ? '#dcf8c6' : '#e0e0e0',
                padding: '10px 15px',
                borderRadius: '20px',
                maxWidth: '70%',
                wordBreak: 'break-word'
              }}
            >
              {renderMessage(msg)}
            </motion.div>
          ))}
        </AnimatePresence>
      </div>

      {/* Input and Buttons */}
      <div style={{ display: 'flex', padding: '15px', borderTop: '1px solid #ccc', gap: '10px' }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          style={{
            flexGrow: 1,
            padding: '10px',
            borderRadius: '20px',
            border: '1px solid #ccc',
            marginRight: '10px'
          }}
        />
        <button
          onClick={sendMessage}
          style={{
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '20px',
            cursor: 'pointer'
          }}
        >
          Send
        </button>
        <button
          onClick={resetChat}
          style={{
            padding: '10px 20px',
            backgroundColor: '#f44336',
            color: 'white',
            border: 'none',
            borderRadius: '20px',
            cursor: 'pointer'
          }}
        >
          Reset
        </button>
      </div>

      {/* Footer */}
      <footer style={{
        backgroundColor: '#4CAF50',
        color: 'white',
        textAlign: 'center',
        padding: '20px 10px',
        borderBottomLeftRadius: '10px',
        borderBottomRightRadius: '10px',
        fontSize: '14px'
      }}>
        <p style={{ marginBottom: '10px' }}>Â© 2025 PEAS - Museum Ticket Guide | All rights reserved</p>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '15px' }}>
          <a href="#" style={{ color: 'white', textDecoration: 'underline' }}>Privacy Policy</a>
          <a href="#" style={{ color: 'white', textDecoration: 'underline' }}>Terms</a>
          <a href="#" style={{ color: 'white', textDecoration: 'underline' }}>Contact Us</a>
        </div>
      </footer>

    </div>
  );
};

export default ChatBot;
