// client/src/About.js

import React from 'react';

function About() {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      
      {/* Hero Section */}
      <section style={{ backgroundColor: '#4CAF50', color: 'white', padding: '4rem 2rem', textAlign: 'center' }}>
        <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>About PEAS</h1>
        <p style={{ fontSize: '1.2rem', maxWidth: '700px', margin: '0 auto' }}>
          Your Smart Ticket Booking Assistant for Museums across Tamil Nadu
        </p>
      </section>

      {/* About Content Section */}
      <section style={{ display: 'flex', flexWrap: 'wrap', padding: '4rem 2rem', alignItems: 'center', gap: '2rem', justifyContent: 'center' }}>
        
        {/* Text Part */}
        <div style={{ flex: '1 1 400px', maxWidth: '500px' }}>
          <h2 style={{ color: '#333', marginBottom: '1rem' }}>Discover Tamil Nadu’s Rich Heritage</h2>
          <p style={{ fontSize: '1.1rem', color: '#555', marginBottom: '1rem' }}>
            PEAS (Personalized Easy Assistant Service) is designed to simplify the way you book museum tickets across Tamil Nadu.
            We bring you closer to the vibrant culture, ancient history, and artistic brilliance of our state — all through a smart and easy chatbot experience.
          </p>
          <p style={{ fontSize: '1.1rem', color: '#555' }}>
            Whether you're a student, tourist, or history enthusiast, PEAS makes your journey seamless — search museums by district, chat for instant details, and book your entry effortlessly.
          </p>
        </div>

        {/* Image Part */}
        <div style={{ flex: '1 1 400px', textAlign: 'center' }}>
          <img 
            src="https://upload.wikimedia.org/wikipedia/commons/e/e7/Government_Museum_Chennai.jpg" 
            alt="Museum Tamil Nadu"
            style={{ width: '100%', maxWidth: '450px', borderRadius: '15px', boxShadow: '0 8px 16px rgba(0,0,0,0.2)' }}
          />
        </div>

      </section>

      {/* Mission Section */}
      <section style={{ backgroundColor: '#f9f9f9', padding: '4rem 2rem', textAlign: 'center' }}>
        <h2 style={{ color: '#4CAF50', marginBottom: '1rem' }}>Our Mission</h2>
        <p style={{ fontSize: '1.2rem', color: '#555', maxWidth: '700px', margin: '0 auto' }}>
          To empower everyone to access Tamil Nadu’s cultural heritage easily, preserving our traditions while making exploration effortless and enjoyable.
        </p>
      </section>

    </div>
  );
}

export default About;
