import React, { useContext } from "react";
import { ThemeContext } from "../App"; // Import the ThemeContext
import ChatBot from "./ChatBot"; // Your existing chatbot

function Chat() {
  const { theme } = useContext(ThemeContext); // Get the current theme from context

  // Define the container style based on the theme
  const containerStyle = {
    padding: "20px",
    backgroundColor: theme === "dark" ? "#121212" : "#f7f7f7", // Dark theme background or default light green
    minHeight: "100vh", // Ensure the container fills the entire height of the screen
    color: theme === "dark" ? "#ffffff" : "#000000", // Dark theme text or default dark text
  };

  // Define the default green and white style for light theme
  const greenWhiteTheme = {
    backgroundColor: "#f7f7f7", // Light background color
    color: "#2E8B57", // Green color for text
  };

  return (
    <div style={{ ...containerStyle, ...(!theme === "dark" && greenWhiteTheme) }}>
      <h2 style={{ textAlign: "center" }}>Start Chatting with PEAS!</h2>
      <ChatBot isDarkTheme={theme === "dark"} /> {/* Pass the theme to ChatBot */}
    </div>
  );
}

export default Chat;
