import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000', {
    transports: ['websocket'],  // Ensure that WebSocket is the only transport
  });

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    socket.on('connect', () => {
      console.log('‚úÖ Connected to socket server:', socket.id);
    });

    socket.on('disconnect', (reason) => {
      console.log(`‚ùå Socket disconnected: ${socket.id} - Reason: ${reason}`);
      // Attempt to reconnect
      socket.connect()
    });

    socket.on('chatMessage', (msg) => {
      setMessages((prev) => [...prev, { text: msg, from: 'bot' }]);
    });

    return () => {
      socket.off('chatMessage');
      socket.disconnect();
    };
  }, []);

  const sendMessage = () => {
    if (input.trim() === '') return;
    setMessages((prev) => [...prev, { text: input, from: 'user' }]);
    socket.emit('chatMessage', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>üéüÔ∏è Museum Ticket Chatbot</h2>
      <div style={{ height: '300px', overflowY: 'auto', marginBottom: '1rem', background: '#f9f9f9', padding: '1rem' }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
            <p style={{
              background: msg.from === 'user' ? '#d1e7dd' : '#e2e3e5',
              display: 'inline-block',
              padding: '8px 12px',
              borderRadius: '12px',
              marginBottom: '6px'
            }}>
              {msg.text}
            </p>
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        placeholder="Type your message..."
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        style={{ width: '80%', padding: '8px' }}
      />
      <button onClick={sendMessage} style={{ padding: '8px 16px', marginLeft: '8px' }}>
        Send
      </button>
    </div>
  );
};

export default ChatBot;
