import React, { useState } from 'react';
import { motion } from 'framer-motion'; // <== NEW

function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/contact', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        alert('Thank you for contacting us! We will get back to you soon.');
        setFormData({ name: '', email: '', message: '' }); // Reset form
      } else {
        alert('Something went wrong. Please try again later.');
      }
    } catch (error) {
      console.error('Error submitting feedback:', error);
      alert('Something went wrong. Please try again later.');
    }
  };

  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', fontFamily: 'Arial, sans-serif' }}>
      {/* Main Content */}
      <div style={{ flexGrow: 1, padding: '3rem' }}>
        
        {/* Header Section */}
        <section style={{ textAlign: 'center', marginBottom: '3rem' }}>
          <h1 style={{ color: '#4CAF50', fontSize: '2.5rem' }}>Contact Us</h1>
          <p style={{ fontSize: '1.2rem', color: '#555', maxWidth: '700px', margin: '0 auto' }}>
            We'd love to hear from you! Whether you have a question about ticket bookings, museums, or anything else,
            our team is ready to answer all your queries.
          </p>
        </section>

        {/* Contact Form Section */}
        <section style={{ maxWidth: '600px', margin: '0 auto' }}>
          <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
            <input 
              type="text"
              name="name"
              placeholder="Your Name"
              value={formData.name}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <input 
              type="email"
              name="email"
              placeholder="Your Email"
              value={formData.email}
              onChange={handleChange}
              required
              style={inputStyle}
            />
            <textarea 
              name="message"
              placeholder="Your Message"
              rows="5"
              value={formData.message}
              onChange={handleChange}
              required
              style={{ ...inputStyle, resize: 'vertical' }}
            />
            {/* Animated Button */}
            <motion.button 
              type="submit"
              style={buttonStyle}
              whileHover={{ scale: 1.05 }}
              transition={{ type: 'spring', stiffness: 300 }}
            >
              Send Message
            </motion.button>
          </form>
        </section>

        {/* Contact Info Section */}
        <section style={{ textAlign: 'center', marginTop: '4rem', color: '#666', fontSize: '1rem' }}>
          <p>📍 Based in Tamil Nadu, India</p>
          <p>✉️ Email: support@peasassistant.com</p>
          <p>📞 Phone: +91-9876543210</p>
        </section>
      </div>

      {/* Footer Section */}
      <footer style={{
        backgroundColor: '#4CAF50',
        color: 'white',
        textAlign: 'center',
        padding: '1.5rem 1rem',
        marginTop: '2rem',
        fontSize: '1rem'
      }}>
        <p style={{ marginBottom: '0.5rem' }}>© 2025 PEAS - Your Museum Ticket Guide</p>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '1.5rem', flexWrap: 'wrap' }}>
          <a href="#" style={footerLinkStyle}>Privacy Policy</a>
          <a href="#" style={footerLinkStyle}>Terms of Service</a>
          <a href="#" style={footerLinkStyle}>Help Center</a>
        </div>
      </footer>
    </div>
  );
}

// Styling
const inputStyle = {
  padding: '1rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '1rem'
};

const buttonStyle = {
  backgroundColor: '#4CAF50',
  color: 'white',
  padding: '1rem',
  border: 'none',
  borderRadius: '8px',
  fontSize: '1.1rem',
  cursor: 'pointer',
  transition: 'background 0.3s'
};

const footerLinkStyle = {
  color: 'white',
  textDecoration: 'none',
  fontSize: '1rem'
};

export default Contact;
