import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';

const socket = io('http://localhost:5000');

function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('bot message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, { sender: 'bot', ...formatMessage(msg) }]);
    });

    return () => socket.off('bot message');
  }, []);

  const formatMessage = (msg) => {
    if (typeof msg === 'object' && msg.type === 'qr') {
      return { type: 'qr', data: msg.data };
    } else {
      return { type: 'text', text: msg };
    }
  };

  const handleSend = () => {
    if (input.trim() !== '') {
      socket.emit('chat message', input);
      setMessages((prevMessages) => [...prevMessages, { sender: 'user', type: 'text', text: input }]);
      setInput('');
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="chat-container" style={styles.chatContainer}>
      <h1 style={styles.title}>PEAS Museum Guide</h1>

      <div className="chat-box" style={styles.chatBox}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
              backgroundColor: msg.sender === 'user' ? '#DCF8C6' : '#F1F0F0',
            }}
          >
            {msg.type === 'qr' ? (
              <img src={msg.data} alt="QR Code" style={{ width: '150px', height: '150px' }} />
            ) : (
              <span>{msg.text}</span>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="input-area" style={styles.inputArea}>
        <input
          style={styles.input}
          type="text"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button style={styles.button} onClick={handleSend}>Send</button>
      </div>
    </div>
  );
}

const styles = {
  chatContainer: {
    width: '500px',
    height: '600px',
    margin: '20px auto',
    display: 'flex',
    flexDirection: 'column',
    border: '1px solid #ccc',
    borderRadius: '8px',
    overflow: 'hidden',
    backgroundColor: '#fff'
  },
  title: {
    margin: 0,
    padding: '10px',
    backgroundColor: '#4CAF50',
    color: 'white',
    fontSize: '20px',
    textAlign: 'center',
  },
  chatBox: {
    flex: 1,
    padding: '10px',
    display: 'flex',
    flexDirection: 'column',
    overflowY: 'auto',
    backgroundColor: '#f9f9f9',
  },
  inputArea: {
    display: 'flex',
    borderTop: '1px solid #ccc',
  },
  input: {
    flex: 1,
    padding: '10px',
    border: 'none',
    outline: 'none',
  },
  button: {
    padding: '10px 20px',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
  },
  message: {
    maxWidth: '70%',
    padding: '8px 12px',
    borderRadius: '15px',
    margin: '5px 0',
    wordBreak: 'break-word',
  },
};

export default ChatBot;
