import React, { useState, useEffect, useRef } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('bot message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, { sender: 'bot', text: msg }]);
    });

    return () => {
      socket.off('bot message');
    };
  }, []);

  useEffect(() => {
    // Auto scroll down when messages update
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = (e) => {
    e.preventDefault();
    if (input.trim() !== '') {
      socket.emit('chat message', input);
      setMessages((prevMessages) => [...prevMessages, { sender: 'user', text: input }]);
      setInput('');
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.chatbox}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
              backgroundColor: msg.sender === 'user' ? '#DCF8C6' : '#E0E0E0',
            }}
          >
            {msg.text.startsWith('data:image') ? (
              <img src={msg.text} alt="QR Code" style={{ width: '150px' }} />
            ) : (
              msg.text
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={handleSend} style={styles.form}>
        <input
          style={styles.input}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button style={styles.button} type="submit">Send</button>
      </form>
    </div>
  );
};

const styles = {
  container: {
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    background: '#f0f0f0',
  },
  chatbox: {
    width: '100%',
    maxWidth: '600px',
    height: '80vh',
    backgroundColor: 'white',
    padding: '10px',
    overflowY: 'auto',
    display: 'flex',
    flexDirection: 'column',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  form: {
    marginTop: '10px',
    display: 'flex',
    width: '100%',
    maxWidth: '600px',
  },
  input: {
    flexGrow: 1,
    padding: '10px',
    borderRadius: '8px 0 0 8px',
    border: '1px solid #ccc',
    outline: 'none',
  },
  button: {
    padding: '10px 20px',
    borderRadius: '0 8px 8px 0',
    border: 'none',
    backgroundColor: '#4CAF50',
    color: 'white',
    cursor: 'pointer',
  },
  message: {
    margin: '5px',
    padding: '10px',
    borderRadius: '10px',
    maxWidth: '80%',
    wordWrap: 'break-word',
  },
};

export default Chatbot;
