import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import { QRCodeCanvas } from 'qrcode.react'; // Import QRCodeCanvas for canvas-based QR code

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [location, setLocation] = useState('');
  const [museum, setMuseum] = useState('');
  const [time, setTime] = useState('');
  const [adults, setAdults] = useState(0);
  const [children, setChildren] = useState(0);

  const [isLocationAsked, setIsLocationAsked] = useState(false);
  const [isMuseumAsked, setIsMuseumAsked] = useState(false);
  const [isPeopleAsked, setIsPeopleAsked] = useState(false);
  const [isTimeAsked, setIsTimeAsked] = useState(false);
  const [hasGreeted, setHasGreeted] = useState(false); // State to track if greeting is sent

  // Generate QR Code for tickets
  const generateTicketQR = () => {
    const qrCodes = [];
    for (let i = 0; i < adults; i++) {
      qrCodes.push(
        <QRCodeCanvas key={i} value={`Ticket: ${museum} | ${location} | ${time} | Adult ${i + 1}`} />
      );
    }
    setMessages((prev) => [
      ...prev,
      { text: `Here are your ${adults} tickets for ${museum} at ${time}.`, from: 'bot' },
    ]);
    setMessages((prev) => [...prev, { text: qrCodes, from: 'bot' }]);
  };

  useEffect(() => {
    socket.on('botMessage', (msg) => {
      setMessages((prev) => [...prev, { text: msg, from: 'bot' }]);
    });

    return () => {
      socket.off('botMessage');
    };
  }, []);

  useEffect(() => {
    // Greet the user only once when the component mounts
    if (!hasGreeted) {
      setMessages((prev) => [...prev, { text: '👋 Hi! Can you please tell me your location?', from: 'bot' }]);
      setHasGreeted(true); // Set greeting to true
    }
  }, [hasGreeted]); // Only trigger when the component is first loaded

  useEffect(() => {
    if (location && !isMuseumAsked) {
      setMessages((prev) => [
        ...prev,
        { text: `Which museum would you like to visit in ${location}?`, from: 'bot' },
      ]);
      setIsMuseumAsked(true);
    }
  }, [location, isMuseumAsked]);

  useEffect(() => {
    if (museum && !isPeopleAsked) {
      setMessages((prev) => [
        ...prev,
        { text: 'How many people are visiting? Please specify the number of adults and children.', from: 'bot' },
      ]);
      setIsPeopleAsked(true);
    }
  }, [museum, isPeopleAsked]);

  useEffect(() => {
    if (adults > 0 && !isTimeAsked) {
      setMessages((prev) => [
        ...prev,
        { text: 'What time will you be visiting the museum?', from: 'bot' },
      ]);
      setIsTimeAsked(true);
    }
  }, [adults, isTimeAsked]);

  useEffect(() => {
    if (time) {
      setMessages((prev) => [
        ...prev,
        { text: 'Generating your ticket(s)... 🎟️', from: 'bot' },
      ]);
      generateTicketQR();  // Now accessible here
    }
  }, [time]);  // No need to add generateTicketQR as a dependency

  const handleSendMessage = () => {
    if (input.trim() === '') return;

    setMessages((prev) => [...prev, { text: input, from: 'user' }]);
    socket.emit('chatMessage', input);
    setInput('');
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
    setMessages((prev) => [...prev, { text: `You are in ${e.target.value}`, from: 'user' }]);
  };

  const handleMuseumChange = (e) => {
    setMuseum(e.target.value);
    setMessages((prev) => [...prev, { text: `You want to visit ${e.target.value}`, from: 'user' }]);
  };

  const handleAdultsChange = (e) => {
    setAdults(e.target.value);
  };

  const handleChildrenChange = (e) => {
    setChildren(e.target.value);
  };

  const handleTimeChange = (e) => {
    setTime(e.target.value);
  };

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>🎟️ Museum Ticket Chatbot</h2>
      <div style={{ height: '300px', overflowY: 'auto', marginBottom: '1rem', background: '#f9f9f9', padding: '1rem' }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
            <p
              style={{
                background: msg.from === 'user' ? '#d1e7dd' : '#e2e3e5',
                display: 'inline-block',
                padding: '8px 12px',
                borderRadius: '12px',
                marginBottom: '6px',
              }}
            >
              {msg.text}
            </p>
          </div>
        ))}
      </div>
      {isLocationAsked && (
        <input
          type="text"
          value={location}
          placeholder="Enter your location"
          onChange={handleLocationChange}
          style={{ width: '80%', padding: '8px' }}
        />
      )}
      {isMuseumAsked && (
        <input
          type="text"
          value={museum}
          placeholder={`Which museum in ${location}?`}
          onChange={handleMuseumChange}
          style={{ width: '80%', padding: '8px' }}
        />
      )}
      {isPeopleAsked && (
        <>
          <input
            type="number"
            value={adults}
            placeholder="Number of adults"
            onChange={handleAdultsChange}
            style={{ width: '80%', padding: '8px' }}
          />
          <input
            type="number"
            value={children}
            placeholder="Number of children"
            onChange={handleChildrenChange}
            style={{ width: '80%', padding: '8px' }}
          />
        </>
      )}
      {isTimeAsked && (
        <input
          type="text"
          value={time}
          placeholder="Enter the timing"
          onChange={handleTimeChange}
          style={{ width: '80%', padding: '8px' }}
        />
      )}
      <button onClick={handleSendMessage} style={{ padding: '8px 16px', marginLeft: '8px' }}>
        Send
      </button>
    </div>
  );
};

export default ChatBot;
