import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('bot message', (message) => {
      setMessages((prev) => [...prev, { sender: 'bot', text: message }]);
    });

    socket.on('bot qr', (qrData) => {
      setMessages((prev) => [...prev, { sender: 'bot', qr: qrData }]);
    });

    return () => {
      socket.off('bot message');
      socket.off('bot qr');
    };
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === '') return;

    setMessages((prev) => [...prev, { sender: 'user', text: input }]);
    socket.emit('chat message', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', marginTop: '30px' }}>
      <h2>Museum Ticket Chatbot</h2>
      <div style={{ border: '1px solid gray', padding: '20px', minHeight: '400px', maxHeight: '500px', overflowY: 'auto' }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{
            textAlign: msg.sender === 'user' ? 'right' : 'left',
            margin: '10px 0'
          }}>
            {msg.text && (
              <span style={{
                display: 'inline-block',
                background: msg.sender === 'user' ? '#c8e6c9' : '#e0e0e0',
                padding: '10px',
                borderRadius: '10px',
                maxWidth: '70%',
                whiteSpace: 'pre-line'
              }}>
                {msg.text}
              </span>
            )}
            {msg.qr && (
              <div style={{ marginTop: '10px' }}>
                <img src={msg.qr} alt="QR Code" style={{ width: '150px' }} />
              </div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div style={{ display: 'flex', marginTop: '10px' }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
          style={{ flexGrow: 1, padding: '10px' }}
        />
        <button onClick={sendMessage} style={{ padding: '10px', background: 'green', color: 'white', border: 'none' }}>
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBot;
