import React, { useContext } from 'react';
import { ThemeContext } from '../ThemeContext'; // Assuming you have a ThemeContext to manage theme
import { motion } from 'framer-motion';

function About() {
  const { theme } = useContext(ThemeContext); // Fetch the current theme from context

  // Conditional styling based on the current theme (light or dark)
  const containerStyle = {
    fontFamily: 'Arial, sans-serif',
    backgroundColor: theme === 'dark' ? '#121212' : '#ffffff', // Dark background for dark theme, white for light theme
    color: theme === 'dark' ? '#eaeaea' : '#000000', // Light text for dark theme, dark text for light theme
  };

  const heroStyle = {
    backgroundColor: theme === 'dark' ? '#2E8B57' : '#4CAF50', // Dark green for dark theme, bright green for light theme
    color: 'white',
    padding: '4rem 2rem',
    textAlign: 'center',
  };

  const heroTitleStyle = {
    fontSize: '3rem',
    marginBottom: '1rem',
  };

  const heroTextStyle = {
    fontSize: '1.2rem',
    maxWidth: '700px',
    margin: '0 auto',
  };

  const sectionStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    padding: '4rem 2rem',
    alignItems: 'center',
    gap: '2rem',
    justifyContent: 'center',
    backgroundColor: theme === 'dark' ? '#1f2937' : '#ffffff', // Dark gray for dark theme, white for light theme
  };

  const textContainerStyle = {
    flex: '1 1 400px',
    maxWidth: '500px',
  };

  const subHeadingStyle = {
    color: theme === 'dark' ? '#90ee90' : '#333', // Light green for dark theme, dark text for light theme
    marginBottom: '1rem',
  };

  const paragraphStyle = {
    fontSize: '1.1rem',
    color: theme === 'dark' ? '#ccc' : '#555', // Light gray for dark theme, darker gray for light theme
    marginBottom: '1rem',
  };

  const imageContainerStyle = {
    flex: '1 1 400px',
    textAlign: 'center',
  };

  const imageStyle = {
    width: '100%',
    maxWidth: '450px',
    borderRadius: '15px',
    boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
  };

  const missionStyle = {
    backgroundColor: theme === 'dark' ? '#111827' : '#f9f9f9', // Dark background for dark theme, light gray for light theme
    padding: '4rem 2rem',
    textAlign: 'center',
  };

  const missionTitleStyle = {
    color: theme === 'dark' ? '#90ee90' : '#4CAF50', // Light green for dark theme, normal green for light theme
    marginBottom: '1rem',
  };

  const missionTextStyle = {
    fontSize: '1.2rem',
    color: theme === 'dark' ? '#ccc' : '#555', // Light gray for dark theme, gray for light theme
    maxWidth: '700px',
    margin: '0 auto',
  };

  return (
    <div style={containerStyle}>
      {/* Hero Section */}
      <section style={heroStyle}>
        <h1 style={heroTitleStyle}>About PEAS</h1>
        <p style={heroTextStyle}>
          Your Smart Ticket Booking Assistant for Museums across Tamil Nadu
        </p>
      </section>

      {/* About Content Section */}
      <section style={sectionStyle}>
        {/* Text Part */}
        <div style={textContainerStyle}>
          <h2 style={subHeadingStyle}>Discover Tamil Nadu’s Rich Heritage</h2>
          <p style={paragraphStyle}>
            PEAS (Personalized Easy Assistant Service) is designed to simplify the way you book museum tickets across Tamil Nadu.
            We bring you closer to the vibrant culture, ancient history, and artistic brilliance of our state — all through a smart and easy chatbot experience.
          </p>
          <p style={paragraphStyle}>
            Whether you're a student, tourist, or history enthusiast, PEAS makes your journey seamless — search museums by district, chat for instant details, and book your entry effortlessly.
          </p>
        </div>

        {/* Image Part */}
        <div style={imageContainerStyle}>
          <motion.img
            src="/images/gandhi-museum-madurai.jpg"
            alt="Museum Tamil Nadu"
            style={imageStyle}
            whileHover={{ scale: 1.05 }}
            transition={{ type: 'spring', stiffness: 300 }}
          />
        </div>
      </section>

      {/* Mission Section */}
      <section style={missionStyle}>
        <h2 style={missionTitleStyle}>Our Mission</h2>
        <p style={missionTextStyle}>
          To empower everyone to access Tamil Nadu’s cultural heritage easily, preserving our traditions while making exploration effortless and enjoyable.
        </p>
      </section>
    </div>
  );
}

export default About;
