import React from "react";
import { useContext } from "react";
import { ThemeContext } from "../App"; // Import the ThemeContext

function Chat() {
  const { theme } = useContext(ThemeContext); // Access the current theme

  const isDark = theme === "dark";

  const themedStyles = {
    container: {
      padding: "40px",
      backgroundColor: isDark ? "#1f2937" : "#f5f5f5",
      color: isDark ? "#f9f9f9" : "#000",
      minHeight: "90vh",
    },
    title: {
      textAlign: "center",
      fontSize: "36px",
      marginBottom: "30px",
      color: isDark ? "#90ee90" : "#2E8B57",
    },
    chatBox: {
      backgroundColor: isDark ? "#2d3748" : "#fff",
      color: isDark ? "#e0e0e0" : "#000",
      padding: "20px",
      borderRadius: "10px",
      boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
      maxWidth: "600px",
      margin: "0 auto",
    },
    inputBox: {
      width: "100%",
      padding: "12px 15px",
      fontSize: "16px",
      borderRadius: "5px",
      border: isDark ? "1px solid #ccc" : "1px solid #aaa",
      marginBottom: "20px",
      backgroundColor: isDark ? "#444" : "#f5f5f5",
      color: isDark ? "#fff" : "#000",
    },
    button: {
      backgroundColor: "#2E8B57",
      color: "white",
      padding: "12px 24px",
      fontSize: "16px",
      border: "none",
      borderRadius: "8px",
      cursor: "pointer",
      display: "block",
      width: "100%",
    },
  };

  return (
    <div style={themedStyles.container}>
      <h2 style={themedStyles.title}>Chat with PEAS</h2>
      <div style={themedStyles.chatBox}>
        <textarea
          style={themedStyles.inputBox}
          rows="5"
          placeholder="Type your message..."
        ></textarea>
        <button style={themedStyles.button}>Send</button>
      </div>
    </div>
  );
}

export default Chat;
