// client/src/Contact.js

import React, { useState } from 'react';

function Contact() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data Submitted:', formData);
    // Here you can later connect it to your backend server if needed
    alert('Thank you for contacting us! We will get back to you soon.');
    setFormData({ name: '', email: '', message: '' });
  };

  return (
    <div style={{ padding: '3rem', fontFamily: 'Arial, sans-serif' }}>
      
      {/* Header Section */}
      <section style={{ textAlign: 'center', marginBottom: '3rem' }}>
        <h1 style={{ color: '#4CAF50', fontSize: '2.5rem' }}>Contact Us</h1>
        <p style={{ fontSize: '1.2rem', color: '#555', maxWidth: '700px', margin: '0 auto' }}>
          We'd love to hear from you! Whether you have a question about ticket bookings, museums, or anything else,
          our team is ready to answer all your queries.
        </p>
      </section>

      {/* Contact Form Section */}
      <section style={{ maxWidth: '600px', margin: '0 auto' }}>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
          
          <input 
            type="text"
            name="name"
            placeholder="Your Name"
            value={formData.name}
            onChange={handleChange}
            required
            style={inputStyle}
          />

          <input 
            type="email"
            name="email"
            placeholder="Your Email"
            value={formData.email}
            onChange={handleChange}
            required
            style={inputStyle}
          />

          <textarea 
            name="message"
            placeholder="Your Message"
            rows="5"
            value={formData.message}
            onChange={handleChange}
            required
            style={{ ...inputStyle, resize: 'vertical' }}
          />

          <button type="submit" style={buttonStyle}>
            Send Message
          </button>

        </form>
      </section>

      {/* Contact Info Section */}
      <section style={{ textAlign: 'center', marginTop: '4rem', color: '#666', fontSize: '1rem' }}>
        <p>üìç Based in Tamil Nadu, India</p>
        <p>‚úâÔ∏è Email: support@peasassistant.com</p>
        <p>üìû Phone: +91-9876543210</p>
      </section>

    </div>
  );
}

// Styling
const inputStyle = {
  padding: '1rem',
  borderRadius: '8px',
  border: '1px solid #ccc',
  fontSize: '1rem'
};

const buttonStyle = {
  backgroundColor: '#4CAF50',
  color: 'white',
  padding: '1rem',
  border: 'none',
  borderRadius: '8px',
  fontSize: '1.1rem',
  cursor: 'pointer',
  transition: 'background 0.3s'
};

export default Contact;
