import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { QRCodeCanvas } from 'qrcode.react';

const socket = io('http://localhost:5000', { transports: ['websocket'] });

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    socket.on('botMessage', (msg) => {
      console.log('Bot says:', msg);
      setMessages(prev => [...prev, { type: 'bot', text: msg }]);
    });

    socket.on('generateTicket', (ticketData) => {
      const qrText = `Location: ${ticketData.location}\nMuseum: ${ticketData.museum}\nTime: ${ticketData.time}\nTicket No: ${ticketData.ticketNumber}`;
      setMessages(prev => [...prev, { type: 'qr', text: qrText }]);
    });

    return () => {
      socket.off('botMessage');
      socket.off('generateTicket');
    };
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (input.trim() === '') return;

    setMessages(prev => [...prev, { type: 'user', text: input.trim() }]);
    socket.emit('userMessage', input.trim());
    setInput('');
  };

  return (
    <div style={styles.chatContainer}>
      <h2>Museum Ticket Chatbot</h2>
      <div style={styles.messagesContainer}>
        {messages.map((msg, idx) => (
          <div key={idx} style={msg.type === 'user' ? styles.userMessage : styles.botMessage}>
            {msg.type === 'qr' ? (
              <>
                <p>Your Ticket:</p>
                <QRCodeCanvas value={msg.text} size={150} />
              </>
            ) : (
              <span>{msg.text}</span>
            )}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit} style={styles.inputForm}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
          style={styles.inputBox}
        />
        <button type="submit" style={styles.sendButton}>Send</button>
      </form>
    </div>
  );
};

const styles = {
  chatContainer: {
    width: '400px',
    margin: '50px auto',
    border: '1px solid #ccc',
    padding: '20px',
    borderRadius: '10px',
    backgroundColor: '#fff',
  },
  messagesContainer: {
    height: '400px',
    overflowY: 'auto',
    border: '1px solid #eee',
    padding: '10px',
    marginBottom: '10px',
    backgroundColor: '#fafafa',
  },
  userMessage: {
    textAlign: 'right',
    margin: '5px 0',
    backgroundColor: '#dcf8c6',
    padding: '8px',
    borderRadius: '8px',
    display: 'inline-block',
    maxWidth: '70%',
  },
  botMessage: {
    textAlign: 'left',
    margin: '5px 0',
    backgroundColor: '#f1f0f0',
    padding: '8px',
    borderRadius: '8px',
    display: 'inline-block',
    maxWidth: '70%',
  },
  inputForm: {
    display: 'flex',
  },
  inputBox: {
    flex: 1,
    padding: '10px',
    fontSize: '16px',
  },
  sendButton: {
    padding: '10px 20px',
    backgroundColor: '#4caf50',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  },
};

export default ChatBot;
