import React from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '../ThemeContext'; // Assuming you have a ThemeContext to manage theme

function About() {
  const { theme } = useTheme(); // Fetch the current theme from the context

  // Conditional styling based on the current theme (light or dark)
  const themedStyles = {
    container: {
      fontFamily: 'Arial, sans-serif',
      backgroundColor: theme === 'dark' ? '#1f2937' : '#ffffff', // Default white for light, dark for dark theme
      color: theme === 'dark' ? '#eaeaea' : '#000', // Light text for light theme, dark for dark theme
    },
    hero: {
      backgroundColor: theme === 'dark' ? '#2E8B57' : '#4CAF50', // Green shade for light, dark green for dark theme
      color: 'white',
      padding: '4rem 2rem',
      textAlign: 'center',
    },
    heroTitle: {
      fontSize: '3rem',
      marginBottom: '1rem',
    },
    heroText: {
      fontSize: '1.2rem',
      maxWidth: '700px',
      margin: '0 auto',
    },
    section: {
      display: 'flex',
      flexWrap: 'wrap',
      padding: '4rem 2rem',
      alignItems: 'center',
      gap: '2rem',
      justifyContent: 'center',
    },
    textContainer: {
      flex: '1 1 400px',
      maxWidth: '500px',
    },
    subHeading: {
      color: theme === 'dark' ? '#90ee90' : '#333', // Light green for dark theme, darker for light theme
      marginBottom: '1rem',
    },
    paragraph: {
      fontSize: '1.1rem',
      color: theme === 'dark' ? '#ccc' : '#555', // Light grey for dark theme, darker grey for light theme
      marginBottom: '1rem',
    },
    imageContainer: {
      flex: '1 1 400px',
      textAlign: 'center',
    },
    image: {
      width: '100%',
      maxWidth: '450px',
      borderRadius: '15px',
      boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
    },
    mission: {
      backgroundColor: theme === 'dark' ? '#111827' : '#f9f9f9', // Dark grey for dark theme, light grey for light theme
      padding: '4rem 2rem',
      textAlign: 'center',
    },
    missionTitle: {
      color: theme === 'dark' ? '#90ee90' : '#4CAF50', // Light green for dark theme, normal green for light theme
      marginBottom: '1rem',
    },
    missionText: {
      fontSize: '1.2rem',
      color: theme === 'dark' ? '#ccc' : '#555', // Light grey for dark theme, grey for light theme
      maxWidth: '700px',
      margin: '0 auto',
    },
  };

  return (
    <div style={themedStyles.container}>
      {/* Hero Section */}
      <section style={themedStyles.hero}>
        <h1 style={themedStyles.heroTitle}>About PEAS</h1>
        <p style={themedStyles.heroText}>
          Your Smart Ticket Booking Assistant for Museums across Tamil Nadu
        </p>
      </section>

      {/* About Content Section */}
      <section style={themedStyles.section}>
        {/* Text Part */}
        <div style={themedStyles.textContainer}>
          <h2 style={themedStyles.subHeading}>Discover Tamil Nadu’s Rich Heritage</h2>
          <p style={themedStyles.paragraph}>
            PEAS (Personalized Easy Assistant Service) is designed to simplify the way you book museum tickets across Tamil Nadu.
            We bring you closer to the vibrant culture, ancient history, and artistic brilliance of our state — all through a smart and easy chatbot experience.
          </p>
          <p style={themedStyles.paragraph}>
            Whether you're a student, tourist, or history enthusiast, PEAS makes your journey seamless — search museums by district, chat for instant details, and book your entry effortlessly.
          </p>
        </div>

        {/* Image Part */}
        <div style={themedStyles.imageContainer}>
          <motion.img
            src="/images/gandhi-museum-madurai.jpg"
            alt="Museum Tamil Nadu"
            style={themedStyles.image}
            whileHover={{ scale: 1.05 }}
            transition={{ type: 'spring', stiffness: 300 }}
          />
        </div>
      </section>

      {/* Mission Section */}
      <section style={themedStyles.mission}>
        <h2 style={themedStyles.missionTitle}>Our Mission</h2>
        <p style={themedStyles.missionText}>
          To empower everyone to access Tamil Nadu’s cultural heritage easily, preserving our traditions while making exploration effortless and enjoyable.
        </p>
      </section>
    </div>
  );
}

export default About;
