import React, { useState, useEffect, useRef } from "react";
import { io } from "socket.io-client";

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [chatEnded, setChatEnded] = useState(false);
  const chatContainerRef = useRef(null);

  useEffect(() => {
    socket.on('bot message', (message) => {
      setMessages((prev) => [...prev, { sender: 'bot', type: 'text', content: message }]);
      if (message.includes('Thank you for booking')) {
        setChatEnded(true);  // End chat when booking finishes
      }
    });

    socket.on('bot qr', (qrImage) => {
      setMessages((prev) => [...prev, { sender: 'bot', type: 'qr', content: qrImage }]);
    });

    return () => {
      socket.off('bot message');
      socket.off('bot qr');
    };
  }, []);

  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  const sendMessage = () => {
    if (input.trim() === '') return;

    setMessages((prev) => [...prev, { sender: 'user', type: 'text', content: input }]);
    socket.emit('chat message', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  const restartChat = () => {
    setMessages([]);
    setInput('');
    setChatEnded(false);
    socket.emit('chat message', 'hi'); // Automatically trigger greeting
  };

  return (
    <div style={{ maxWidth: '600px', margin: 'auto', marginTop: '30px' }}>
      <h2>Museum Ticket Chatbot</h2>
      <div 
        style={{ 
          border: '1px solid gray', 
          padding: '20px', 
          minHeight: '400px', 
          maxHeight: '400px', 
          overflowY: 'auto', 
          display: 'flex', 
          flexDirection: 'column' 
        }}
        ref={chatContainerRef}
      >
        {messages.map((msg, idx) => (
          <div 
            key={idx} 
            style={{
              textAlign: msg.sender === 'user' ? 'right' : 'left',
              margin: '10px 0'
            }}
          >
            {msg.type === 'text' ? (
              <span style={{
                display: 'inline-block',
                background: msg.sender === 'user' ? '#c8e6c9' : '#e0e0e0',
                padding: '10px',
                borderRadius: '10px',
                maxWidth: '70%',
                whiteSpace: 'pre-line'
              }}>
                {msg.content}
              </span>
            ) : (
              <img src={msg.content} alt="QR Code" style={{ maxWidth: '200px', marginTop: '10px' }} />
            )}
          </div>
        ))}
      </div>

      {!chatEnded && (
        <div style={{ display: 'flex', marginTop: '10px' }}>
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Type your message..."
            style={{ flexGrow: 1, padding: '10px' }}
          />
          <button 
            onClick={sendMessage} 
            style={{ padding: '10px', background: 'green', color: 'white', border: 'none' }}
          >
            Send
          </button>
        </div>
      )}

      {chatEnded && (
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button 
            onClick={restartChat} 
            style={{ padding: '10px 20px', background: 'blue', color: 'white', border: 'none', borderRadius: '8px' }}
          >
            Restart Chat
          </button>
        </div>
      )}
    </div>
  );
};

export default ChatBot;
