// client/src/components/ChatBot.js
import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';
import { QRCodeCanvas } from 'qrcode.react';// Import for QR code generation

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [location, setLocation] = useState('');
  const [museum, setMuseum] = useState('');
  const [time, setTime] = useState('');
  const [adults, setAdults] = useState(0);
  const [children, setChildren] = useState(0);
  const [isLocationAsked, setIsLocationAsked] = useState(false);
  const [isMuseumAsked, setIsMuseumAsked] = useState(false);
  const [isTimeAsked, setIsTimeAsked] = useState(false);
  const [isPeopleAsked, setIsPeopleAsked] = useState(false);
  const [hasGreeted, setHasGreeted] = useState(false); // Track if the bot has greeted the user

  useEffect(() => {
    socket.on('botMessage', (msg) => {
      console.log('📡 Listening for botMessage...');
      console.log('🤖 Bot says:', msg);
      setMessages((prev) => [...prev, { text: msg, from: 'bot' }]);
    });

    return () => {
      socket.off('botMessage');
    };
  }, []);

  const sendMessage = () => {
    if (input.trim() === '') return;
    setMessages((prev) => [...prev, { text: input, from: 'user' }]);
    socket.emit('chatMessage', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  // Bot Flow Functions
  const startConversation = () => {
    setMessages([{ text: '👋 Hello! How can I assist you today?', from: 'bot' }]);
    setHasGreeted(true);
    setIsLocationAsked(true);
  };

  const askLocation = (locationInput) => {
    setLocation(locationInput);
    socket.emit('chatMessage', `User is in ${locationInput}`);
    setMessages((prev) => [
      ...prev,
      { text: `Which museum would you like to visit in ${locationInput}?`, from: 'bot' },
    ]);
    setIsMuseumAsked(true);
  };

  const askMuseum = (museumInput) => {
    setMuseum(museumInput);
    socket.emit('chatMessage', `User wants to visit the ${museumInput} museum`);
    setMessages((prev) => [
      ...prev,
      { text: `What time would you prefer to visit the ${museumInput} museum?`, from: 'bot' },
    ]);
    setIsTimeAsked(true);
  };

  const askTime = (timeInput) => {
    setTime(timeInput);
    socket.emit('chatMessage', `User wants to visit the museum at ${timeInput}`);
    setMessages((prev) => [
      ...prev,
      { text: 'How many people (adults and children)?', from: 'bot' },
    ]);
    setIsPeopleAsked(true);
  };

  const askPeople = () => {
    setMessages((prev) => [
      ...prev,
      { text: 'Please specify the number of adults and children.', from: 'bot' },
    ]);
  };

  const handleAdultsChange = (e) => {
    setAdults(Number(e.target.value));
  };

  const handleChildrenChange = (e) => {
    setChildren(Number(e.target.value));
  };

  const generateTicketQR = () => {
    // Generate QR codes for adults
    const qrCodes = [];
    for (let i = 0; i < adults; i++) {
      qrCodes.push(
        <QRCode key={i} value={`Ticket: ${museum} | ${location} | ${time} | Adult ${i + 1}`} />
      );
    }
    return qrCodes;
  };

  // Handle User Input Based on Flow
  useEffect(() => {
    if (!hasGreeted) {
      startConversation(); // Greet the user and start the conversation
    }

    if (isLocationAsked && location) {
      askLocation(location);
    }

    if (isMuseumAsked && museum) {
      askMuseum(museum);
    }

    if (isTimeAsked && time) {
      askTime(time);
    }

    if (isPeopleAsked && adults !== null && children !== null) {
      generateTicketQR();
    }
  }, [messages, location, museum, time, adults, children, hasGreeted, isLocationAsked, isMuseumAsked, isTimeAsked, isPeopleAsked]);

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>🎟️ Museum Ticket Chatbot</h2>
      <div style={{ height: '300px', overflowY: 'auto', marginBottom: '1rem', background: '#f9f9f9', padding: '1rem' }}>
        {messages.map((msg, i) => (
          <div key={i} style={{ textAlign: msg.from === 'user' ? 'right' : 'left' }}>
            <p
              style={{
                background: msg.from === 'user' ? '#d1e7dd' : '#e2e3e5',
                display: 'inline-block',
                padding: '8px 12px',
                borderRadius: '12px',
                marginBottom: '6px',
              }}
            >
              {msg.text}
            </p>
            {i === messages.length - 1 && adults > 0 && generateTicketQR()}
          </div>
        ))}
      </div>
      <input
        type="text"
        value={input}
        placeholder="Type your message..."
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        style={{ width: '80%', padding: '8px' }}
      />
      <button onClick={sendMessage} style={{ padding: '8px 16px', marginLeft: '8px' }}>
        Send
      </button>

      {/* Show input for adults and children */}
      {isPeopleAsked && (
        <div>
          <input
            type="number"
            value={adults}
            onChange={handleAdultsChange}
            placeholder="Number of adults"
            style={{ marginTop: '10px', width: '80%', padding: '8px' }}
          />
          <input
            type="number"
            value={children}
            onChange={handleChildrenChange}
            placeholder="Number of children"
            style={{ marginTop: '10px', width: '80%', padding: '8px' }}
          />
        </div>
      )}
    </div>
  );
};

export default ChatBot;
