// client/src/components/ChatBot.js
import React, { useEffect, useState } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  useEffect(() => {
    // Listen for bot reply
    socket.on('botMessage', (msg) => {
      console.log('üì° Bot message:', msg);
      setMessages((prev) => [...prev, { text: msg, from: 'bot' }]);
    });

    return () => {
      socket.off('botMessage');
    };
  }, []);

  const sendMessage = () => {
    if (input.trim() === '') return;
    setMessages((prev) => [...prev, { text: input, from: 'user' }]);
    socket.emit('chatMessage', input);
    setInput('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  const renderMessage = (msg, i) => {
    const isBot = msg.from === 'bot';
    const isQR = msg.text.includes('<img'); // Check for QR code

    return (
      <div key={i} style={{ textAlign: isBot ? 'left' : 'right' }}>
        <div
          style={{
            background: isBot ? '#e2e3e5' : '#d1e7dd',
            display: 'inline-block',
            padding: '8px 12px',
            borderRadius: '12px',
            marginBottom: '6px',
            maxWidth: '90%',
            overflowWrap: 'break-word',
          }}
        >
          {isQR ? (
            <div dangerouslySetInnerHTML={{ __html: msg.text }} />
          ) : (
            msg.text
          )}
        </div>
      </div>
    );
  };

  return (
    <div style={{ maxWidth: '600px', margin: '2rem auto', padding: '1rem', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>üéüÔ∏è Museum Ticket Chatbot</h2>
      <div style={{ height: '300px', overflowY: 'auto', marginBottom: '1rem', background: '#f9f9f9', padding: '1rem' }}>
        {messages.map(renderMessage)}
      </div>
      <input
        type="text"
        value={input}
        placeholder="Type your message..."
        onChange={(e) => setInput(e.target.value)}
        onKeyPress={handleKeyPress}
        style={{ width: '80%', padding: '8px' }}
      />
      <button onClick={sendMessage} style={{ padding: '8px 16px', marginLeft: '8px' }}>
        Send
      </button>
    </div>
  );
};

export default ChatBot;
