import React from 'react';
import { motion } from 'framer-motion';
import { useTheme } from '../ThemeContext'; // Import useTheme

function About() {
  const { theme } = useTheme(); // Get the current theme

  const themedStyles = {
    container: {
      fontFamily: 'Arial, sans-serif',
    },
    hero: {
      backgroundColor: theme === 'dark' ? '#121212' : '#fff', // Dark or light theme background
      color: theme === 'dark' ? '#fff' : '#000', // Dark or light theme text color
      padding: '4rem 2rem',
      textAlign: 'center',
    },
    heroTitle: {
      fontSize: '3rem',
      marginBottom: '1rem',
    },
    heroText: {
      fontSize: '1.2rem',
      maxWidth: '700px',
      margin: '0 auto',
    },
    section: {
      display: 'flex',
      flexWrap: 'wrap',
      padding: '4rem 2rem',
      alignItems: 'center',
      gap: '2rem',
      justifyContent: 'center',
    },
    subHeading: {
      color: theme === 'dark' ? '#90ee90' : '#333',
      marginBottom: '1rem',
    },
    paragraph: {
      fontSize: '1.1rem',
      color: theme === 'dark' ? '#ccc' : '#555',
      marginBottom: '1rem',
    },
  };

  return (
    <div style={themedStyles.container}>
      {/* Hero Section */}
      <section style={themedStyles.hero}>
        <h1 style={themedStyles.heroTitle}>About PEAS</h1>
        <p style={themedStyles.heroText}>
          Your Smart Ticket Booking Assistant for Museums across Tamil Nadu
        </p>
      </section>

      {/* About Content Section */}
      <section style={themedStyles.section}>
        {/* Text Part */}
        <div style={{ flex: '1 1 400px', maxWidth: '500px' }}>
          <h2 style={themedStyles.subHeading}>Discover Tamil Nadu’s Rich Heritage</h2>
          <p style={themedStyles.paragraph}>
            PEAS (Personalized Easy Assistant Service) is designed to simplify the way you book museum tickets across Tamil Nadu.
            We bring you closer to the vibrant culture, ancient history, and artistic brilliance of our state — all through a smart and easy chatbot experience.
          </p>
          <p style={themedStyles.paragraph}>
            Whether you're a student, tourist, or history enthusiast, PEAS makes your journey seamless — search museums by district, chat for instant details, and book your entry effortlessly.
          </p>
        </div>

        {/* Image Part */}
        <div style={{ flex: '1 1 400px', textAlign: 'center' }}>
          <motion.img
            src="/images/gandhi-museum-madurai.jpg"
            alt="Museum Tamil Nadu"
            style={{ width: '100%', maxWidth: '450px', borderRadius: '15px', boxShadow: '0 8px 16px rgba(0,0,0,0.2)' }}
            whileHover={{ scale: 1.05 }}
            transition={{ type: 'spring', stiffness: 300 }}
          />
        </div>
      </section>

      {/* Mission Section */}
      <section style={{ backgroundColor: theme === 'dark' ? '#111827' : '#f9f9f9', padding: '4rem 2rem', textAlign: 'center' }}>
        <h2 style={{ color: theme === 'dark' ? '#90ee90' : '#4CAF50', marginBottom: '1rem' }}>Our Mission</h2>
        <p style={{ fontSize: '1.2rem', color: theme === 'dark' ? '#ccc' : '#555', maxWidth: '700px', margin: '0 auto' }}>
          To empower everyone to access Tamil Nadu’s cultural heritage easily, preserving our traditions while making exploration effortless and enjoyable.
        </p>
      </section>
    </div>
  );
}

export default About;
